import { useState } from "react";
import styled from "styled-components";

import Modal from "./components/Modal";

import designLogo from "./images/Figma.png";
import frontLogo from "./images/React.png";
import backLogo from "./images/django.png";

const BabyLionList = {
  "Í∏∞Ìöç & ÎîîÏûêÏù∏": [
    { id: 1, name: "ÍπÄÏàòÎØº", major: "ÎîîÏûêÏù∏ÌïôÎ∂Ä"},
    { id: 2, name: "ÍπÄÏßÄÌõÑ", major: "ÏúµÌï©ÏΩòÌÖêÏ∏†ÌïôÍ≥º" },
    { id: 3, name: "Î∞ïÏÑúÏòÅ", major: "Ïä§Ìè¨Ï∏†Í≥ºÌïôÍ≥º" },
    { id: 4, name: "ÏÜ°ÎÇòÏòÅ", major: "Ï§ëÏñ¥Ï§ëÎ¨∏ÌïôÍ≥º" },
    { id: 5, name: "Ïù¥ÎèÑÏú§", major: "Í≤ΩÏòÅÌïôÎ∂Ä" },
    { id: 6, name: "Ïù¥ÏùÄÏÑú", major: "ÏúµÌï©ÏΩòÌÖêÏ∏†ÌïôÍ≥º" },
  ],
  ÌîÑÎ°†Ìä∏ÏóîÎìú: [
    { id: 7, name: "ÍπÄÎØºÌòú", major: "Ï†ÑÍ∏∞Ï†ÑÏûêÍ≥µÌïôÍ≥º" },
    { id: 8, name: "ÍπÄÏÑ±Ï£º", major: "ÏÇ¨Ïù¥Î≤ÑÎ≥¥ÏïàÌïôÍ≥º" },
    { id: 9, name: "ÏÑúÏòàÎ¶∞", major: "Ïª¥Ìì®ÌÑ∞Í≥µÌïôÍ≥º" },
    { id: 10, name: "Ïú†ÏßÑÏÑú", major: "Ïª¥Ìì®ÌÑ∞Í≥µÌïôÍ≥º" },
    { id: 11, name: "Ïù¥ÏòàÏòÅ", major: "ÍµêÏú°Í≥µÌïôÍ≥º" },
    { id: 12, name: "ÏµúÏó∞ÏÑú", major: "Ïù∏Í≥µÏßÄÎä•ÌïôÍ≥º" },
  ],
  Î∞±ÏóîÎìú: [
    { id: 13, name: "ÍπÄÍ∞ÄÏòÅ", major: "Ïª¥Ìì®ÌÑ∞Í≥µÌïôÍ≥º" },
    { id: 14, name: "ÍπÄÏó∞Ïö∞", major: "Ïª¥Ìì®ÌÑ∞Í≥µÌïôÍ≥º" },
    { id: 15, name: "ÏÑ±ÌòúÎ¶∞", major: "ÏÇ¨Ïù¥Î≤ÑÎ≥¥ÏïàÌïôÍ≥º" },
    { id: 16, name: "ÏÑ§ÏòÅÏùÄ", major: "Í≥ºÌïôÍµêÏú°Í≥º" },
    { id: 17, name: "Ïã†ÏßÄÎØº", major: "Ïª¥Ìì®ÌÑ∞Í≥µÌïôÍ≥º" },
    { id: 18, name: "Ìô©Í∑úÎ¶¨", major: "Ïª¥Ìì®ÌÑ∞Í≥µÌïôÍ≥º" },
  ]
};

function App() {
  const [modal, setModal] = useState(false);
  const [track, setTrack] = useState("");
  const [trackData, setTrackData] = useState([]);
  const [id, setId] = useState("");

  const selectedLionName = trackData.find((lion) => lion.id === id)?.name || "";

  const selectTrack = (selectedTrack) => {
    setTrack(selectedTrack);
    setTrackData(BabyLionList[selectedTrack]);
    setId("");
  }

  return (
    <Wrapper>
      <BackgroundModal>
        <Title>
          <p className="h1">react1 homework</p>
          <p className="h2">ü´≥ÏïÑÍ∏∞ÏÇ¨ÏûêÎ•º ÏùëÏõêÌï¥Ïöîü´≥</p>
          <div className="Line"></div>
        </Title>

        <TrackOption>
        <p>Ìä∏ÎûôÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
        <div className="chooseTrack">
          <TrackBtn onClick={() => selectTrack("Í∏∞Ìöç & ÎîîÏûêÏù∏")}>
          <img src={designLogo} alt="Í∏∞ÌöçÎîîÏûêÏù∏" />
          <span>Í∏∞ÌöçÎîîÏûêÏù∏</span>
          </TrackBtn>
          <TrackBtn onClick={() => selectTrack("ÌîÑÎ°†Ìä∏ÏóîÎìú")}>
          <img src={frontLogo} alt="ÌîÑÎ°†Ìä∏ÏóîÎìú" />
          <span>ÌîÑÎ°†Ìä∏ÏóîÎìú</span>
          </TrackBtn>
          <TrackBtn onClick={() => selectTrack("Î∞±ÏóîÎìúÏù∏")}>
          <img src={backLogo} alt="Î∞±ÏóîÎìú" />
          <span>Î∞±ÏóîÎìú</span>
          </TrackBtn>
        </div>
        </TrackOption>

        <OpenBtn onClick={() => setModal(true)}>Î™®Îã¨ ÎßåÎì§Í∏∞</OpenBtn>
        {modal && (
          <Modal
          setModal={setModal}
          track={track}
          datalist={BabyLionList[track]}
          id={id}
          setId={setId}
          />
        )}
      </BackgroundModal>
      <Cheering>
        {track}
        {selectedLionName}
        ÏïÑÍ∏∞ÏÇ¨ÏûêÎ•º ÏùëÏõêÌï©ÎãàÎã§!</Cheering>
    </Wrapper>
  );
}

export default App;

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;

  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  gap: 2rem;
  background-color: #d8e4ec;
  font-family: "Pretendard";
  white-space: nowrap;
`;

const BackgroundModal = styled.div`
  height: 40%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 3rem 2rem;

  background: #ffffff;
  box-shadow: 0px 5px 15px rgba(0. 0, 0, 0.25);
  border-radius: 24px;
  font-family: "Pretendard";

  @media (max-width: 1100px) {
    width: 60%;
  }

  @media (min-width: 1100px) {
    width: 50%;
  }
`;

const Title = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-bottom: 1.5px solid #d7d7d7;

  .h1 {
    font-weight: 300;
    font-size: 15px;
    margin-bottom: 0;
  }

  .h2 {
    font-weight: 600;
    font-size: 2rem;
    margin-top: 10px;
  }
`;

const TrackOption = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;

  p {
    font-family: "Pretendard";
    font-style: normal;
    color: #414141;
    font-weight: 700;
    font-size: 22px;
    line-height: 30px;
  }

  .chooseTrack {
    background-color: transparent;
    border: none;

    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
  }
`;

const TrackBtn = styled.button`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  height: 100px;
  background-color: transparent;
  border: none;

  img {
    width: 60px;
    height: 60px;
    flex-shrink: 0;
    cursor: pointer;
  }
`;

const OpenBtn = styled.button`
  padding: 0.5rem 2rem;
  font-family: "Pretendard";
  color: #ffffff;
  background: #81a7d3;
  border-radius: 1.3rem;
  border: none;
  font-size: 20px;
  margin-top: 5%;
  cursor: pointer;
`;

const Cheering = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 1rem 2rem;
  border-radius: 24px;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.25);

  background: #ffffff;
  color: #81a7d3;
  font-family: "Pretendard";

  @media (max-width: 1100px) {
    width: 60%;
  }
  @media (min-width: 1100px) {
    width: 50%;
  }
`;
