import React, { useState } from 'react';

function App() {
  const [selected, setSelected] = useState(null);

  const handleClick = (id) => {
    setSelected(id);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <p style={styles.subtitle}>NEXT 13th NETWORK</p>
        <h1 style={styles.title}>‚ú® ÏïÑÍ∏∞ÏÇ¨ÏûêÌåÄÏùÑ ÏùëÏõêÌï¥Ïöî ‚ú®</h1>

        <p style={styles.instruction}>Î°úÍ≥†Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
        <div style={styles.options}>
          {['Í∏∞ÏàòÏÇ¨ÏûêÎãò', 'ÌîÑÎ°†Ìä∏ÏóîÎìú', 'Î∞±ÏóîÎìú'].map((label, idx) => (
            <div
              key={label}
              onClick={() => handleClick(idx)}
              style={{
                ...styles.optionItem,
                transform: selected === idx ? 'scale(1.1)' : 'scale(1)',
                opacity: selected === idx ? 1 : 0.6,
              }}
            >
              <div style={styles.circle}></div>
              <p style={styles.optionLabel}>{label}</p>
            </div>
          ))}
        </div>

        <button style={styles.button}>ÏÑ†ÌÉù ÏôÑÎ£åÌïòÍ∏∞</button>
      </div>

      <button style={styles.link}>ÏïÑÍ∏∞ÏÇ¨ÏûêÎ•º Î≥¥Îü¨Í∞ÄÎ≥¥Ïûê!</button>
    </div>
  );
}

// üé® Ïù∏ÎùºÏù∏ Ïä§ÌÉÄÏùº
const styles = {
  container: {
    backgroundColor: '#dce6ed',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  },
  card: {
    backgroundColor: 'white',
    padding: '30px',
    borderRadius: '20px',
    textAlign: 'center',
    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
    width: '90%',
    maxWidth: '400px',
  },
  subtitle: {
    fontSize: '12px',
    color: '#888',
  },
  title: {
    fontSize: '20px',
    fontWeight: 'bold',
    margin: '10px 0 20px',
  },
  instruction: {
    marginBottom: '10px',
    fontWeight: '500',
  },
  options: {
    display: 'flex',
    justifyContent: 'space-around',
    marginBottom: '20px',
  },
  optionItem: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    cursor: 'pointer',
    transition: 'all 0.2s',
  },
  circle: {
    width: '50px',
    height: '50px',
    backgroundColor: '#bbb',
    borderRadius: '50%',
    marginBottom: '5px',
  },
  optionLabel: {
    fontSize: '14px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#4098f7',
    color: 'white',
    border: 'none',
    borderRadius: '999px',
    cursor: 'pointer',
    fontWeight: 'bold',
  },
  link: {
    marginTop: '20px',
    background: 'white',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '999px',
    cursor: 'pointer',
    color: '#4098f7',
    fontWeight: 'bold',
  },
};

export default App;
